mapper.select:
SELECT COUNT(*)
FROM approval 
WHER doc_cat_cd = #{docCatCd}

mapper.update:
UPDATE approval
SET aprv_yn = '1', 
	aprv_ymd = NOW(), 
	signature = #{signature} 
WHERE aprv_no = #{aprv_no} 
AND user_id = #{user_id};

UPDATE approval
SET aprv_stat_cd = #{withdrawnStatus},
	aprv_ymd = NOW(),
	rjct_reason = #{withdrawReason}
WHERE aprv_no = #{aprv_no} 
AND user_id = #{loginId};
