AND bkg_stat_cd IN (SELECT code FROM common_code WHERE group_code = 'B101' AND code_nm in
#{item}
)
AND trgt_cat_cd = (SELECT code FROM common_code WHERE group_code = 'B102' AND code_nm = #{searchCat})
